import React, { useContext } from 'react';
import { ActivityIndicator, Text, TouchableOpacity, } from 'react-native';
import { ConfirmContext } from '../context';
import getStyles from './styles';
const EnhancedButton = ({ children, onPress, style, labelStyle, inverse, loading }) => {
    const { theme, buttonStyle, buttonLabelStyle, } = useContext(ConfirmContext);
    const styles = getStyles(theme);
    return (<TouchableOpacity style={[
        styles.button,
        inverse && styles.inverse,
        style,
        buttonStyle,
    ]} onPress={onPress}>
      {loading &&
        <ActivityIndicator color={inverse ? 'black' : 'white'} style={styles.loadingIndicator}/>}
      <Text style={[styles.label,
        inverse && styles.inverseLabel,
        labelStyle,
        buttonLabelStyle
    ]}>
        {children}
      </Text>
    </TouchableOpacity>);
};
export default EnhancedButton;
